import Vue from 'vue'
import Router from 'vue-router'

// 使用路由插件
Vue.use(Router)

//導入組件
import Main from './pages/main'

//實例化路由
let router = new Router({
    mode: 'hash', //1.hash:url帶#號2history歷史:url不戴#號
    base: process.env.BASE_URL,
    routes: [
        {
            path: "/", //url路徑"/"代表首頁, http://localhost:8080/
            name: 'main', //路由的名字
            component: Main
        }
    ]
})

export default router
